import * as THREE from 'three'
import React, {useRef, useMemo} from 'react'
import {createPortal, useFrame, useThree} from 'react-three-fiber'
import {Text, OrthographicCamera} from 'drei'

import '../materials/TypeMaskMaterial'

function TypeMaskPlane(props) {
    const cam = useRef()
    const material = useRef()
    const content = 'BOX'

    const [scene, target] = useMemo(() => {
        const scene = new THREE.Scene()
        scene.background = new THREE.Color('white')
        const target = new THREE.WebGLRenderTarget(2048, 2048)
        return [scene, target]
    }, [])

    const startTime = Date.now()

    useFrame((state) => {
        state.gl.setRenderTarget(target)
        state.gl.render(scene, cam.current)
        state.gl.setRenderTarget(null)

        material.current.uTime = Date.now() - startTime
    })

    return (
        <>
            <OrthographicCamera ref={cam} position={[0, 0, 10]} zoom={10} />
            {
                // createPortal: Render children into a DOM node that exists outside the DOM hierarchy of the parent component.
                createPortal(
                    // Drei's Troika Text engine
                    <Text
                        color={0x000000}
                        fontSize={40}
                        maxWidth={window.width}
                        lineHeight={1.0}
                        letterSpacing={-0.08}
                        textAlign={'justify'}
                        font="https://fonts.gstatic.com/s/raleway/v14/1Ptrg8zYS_SKggPNwK4vaqI.woff"
                        anchorX="center"
                        anchorY="middle"
                    >
                        {content}
                    </Text>,
                    scene
                )
            }
            <mesh {...props}>
                {/* Use plane to size text appropriately */}
                <planeBufferGeometry attach="geometry" args={[1, 1]} />
                {/* <boxBufferGeometry attach="geometry" args={[3, 3, 3]} /> */}
                <typeMaskMaterial
                    ref={material}
                    attach="material"
                    uTexture={target.texture}
                />
            </mesh>
        </>
    )
}

export default TypeMaskPlane
