import React, {useRef, useEffect, useState} from 'react'
import * as THREE from 'three'
import {useFrame, useThree} from 'react-three-fiber'

import '../materials/LightbarMaterial'

// re-use for instance computations
const scratchObject3D = new THREE.Object3D()

function LightbarsInstanced(props) {
    const meshRef = useRef()
    const numPoints = 10

    // update instance matrices only when needed
    React.useEffect(() => {
        const mesh = meshRef.current

        // set the transform matrix for each instance
        for (let i = 0; i < numPoints; ++i) {
            const x = (i % 30) * 1.05
            const y = Math.floor(i / 30) * 1.05
            const z = 0

            scratchObject3D.position.set(x, y, z)
            scratchObject3D.rotation.set(0.5 * Math.PI, 0, 0) // cylinders face z direction
            scratchObject3D.updateMatrix()
            mesh.setMatrixAt(i, scratchObject3D.matrix)
        }

        mesh.instanceMatrix.needsUpdate = true
    }, [numPoints])

    return (
        <instancedMesh ref={meshRef} args={[null, null, numPoints]} frustumCulled={false}>
            <cylinderBufferGeometry attach="geometry" args={[100, 100, 0.15, 32]} />
            <meshStandardMaterial attach="material" color="#fff" />
        </instancedMesh>
    )
}

export default LightbarsInstanced
