import React, {useRef, useEffect, useState} from 'react'
import * as THREE from 'three'
import {useFrame} from 'react-three-fiber'

const tempObject = new THREE.Object3D()

function LightbarsInstanced(props) {
    const [hovered, setHovered] = useState()

    const ref = useRef()
    const previous = useRef()

    const {viewport} = useThree()

    const height = viewport.height * state.pages
    const width = viewport.width / 10

    useEffect(() => void (previous.current = hovered), [hovered])

    useFrame((state) => {
        const time = state.clock.getElapsedTime()

        let i = 0
        for (let x = 0; x < 10; x++) {
            const id = i++
            tempObject.position.set(x, 0, 0)

            if (hovered !== previous.current) {
                console.log('hovered')
                ref.current.geometry.attributes.color.needsUpdate = true
            }
        }
        ref.current.instanceMatrix.needsUpdate = true
    })

    return (
        <instancedMesh
            ref={ref}
            args={[null, null, 10]}
            onPointerMove={(e) => setHovered(e.instanceId)}
            onPointerOut={(e) => setHovered(undefined)}
        >
            <planeBufferGeometry attach="geometry" args={[10, props.bar.height]} />
            <meshPhongMaterial attach="material" vertexColors={THREE.VertexColors} />
        </instancedMesh>
    )
}

