import React, {useRef, useEffect, useState} from 'react'
import * as THREE from 'three'
import {useFrame, useThree} from 'react-three-fiber'
import lerp from 'lerp'

import '../materials/LightbarMaterial'

// re-use for instance computations
const tempObject = new THREE.Object3D()

function LightbarsInstanced(props) {
    const meshRef = useRef()
    const numPoints = 10

    const {viewport} = useThree()
    const barWidth = viewport.width / 10
    const barHeight = viewport.height
    const [minXPos, maxXPos] = [-viewport.width / 1.75, viewport.width / 1.75]
    const [minInterval, maxInterval] = [1000, 5000]

    // array of old and new postions for each bar
    const positions = new Array(numPoints).fill([0, 0])

    // returns random float between lower and upper bounds
    function random(lowerBound, upperBound) {
        return lowerBound + Math.random() * (upperBound + 1 - lowerBound)
    }

    // random position for each bar at random intervals
    for (let i = 0; i < numPoints; ++i) {
        function doSomething() {
            position[i][0] = position[i][1]
            positions[i][1] = random(minXPos, maxXPos)
        }

        ;(function loop() {
            setTimeout(function () {
                doSomething()
                loop()
            }, random(minInterval, maxInterval))
        })()
    }

    // lerp bar to its new position
    useFrame(() => {
        const mesh = meshRef.current

        for (let i = 0; i < numPoints; ++i) {
            tempObject.position.x = lerp(0, positions[i], 0.1)
            tempObject.updateMatrix()
            mesh.setMatrixAt(i, tempObject.matrix)
        }
        mesh.instanceMatrix.needsUpdate = true
    })

    return (
        <instancedMesh ref={meshRef} args={[null, null, numPoints]} frustumCulled={false}>
            <planeBufferGeometry attach="geometry" args={[barWidth, barHeight]} />

            <meshStandardMaterial attach="material" color="#fff" />
        </instancedMesh>
    )
}

export default LightbarsInstanced
